//  };
  function createPost(title, description, name, email) {
    const data = {
        title: title,
        description: description,
        name: name,
        email: email
    };
    axios.post('/localhost:3000/posts', data)
        .then(res => console.log(res))
        .catch(err => console.log(err));
}

 
export default CreerPost
