import React, {useState} from 'react'
import '../index.css';
import '../App.css';
import Navbar from '../Navbar/Navbar';
import axios from 'axios';



function Inscription() {
  const url ="http://localhost:3000/users"
  const [data, setData] = useState({
    userId:"",
    name:"",
    lastName:"",
    age:"",
    email:"",
    password:"",

  });
function submit(e){
  e.preventDefault();
  axios.post(url,{
    userId: data.userId,
    name: data.name,
    lastName: data.lastName,
    age: data.age,
    email: data.email,
    password: data.password,
  })
  .then(res=>{
    console.log(res.data)
  })
}

  function handle(e){
const newdata={...data}
newdata[e.target.id] = e.target.value
setData(newdata)
console.log(newdata)
  };

  function Connexion() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
  
    function handleEmailChange(event) {
      setEmail(event.target.value);
    }
  
    function handlePasswordChange(event) {
      setPassword(event.target.value);
    }
  
    function handleSubmit(e) {
       e.preventDefault();
       const { email, password } = data;
       axios.get("http://localhost:3000/users")
         .then((res) => {
           const users = res.data;
           const authenticatedUser = users.find(
             (user) => user.email === email && user.password === password
           );
           if (authenticatedUser) {
             window.location.href = "/dashboard";
           } else {
             alert("Email ou mot de passe incorrect.");
           }
         });
     }
     
  
    return (
      <div className="bg-grey-lighter min-h-screen flex flex-col">

      </div>
    );
  }
  
  
    return (
      <div className="bg-grey-lighter min-h-screen flex flex-col">
        <div className="container max-w-sm mx-auto flex-1 flex flex-col items-center justify-center px-2 scrollable-container">
          <form
            className="bg-white px-6 py-8 rounded shadow-md text-black w-full"
            onSubmit={(e)=> submit(e)}>
            <h1 className="mb-8 text-3xl text-center">Sign up</h1>

            <input
             onChange={(e)=>handle(e)} placeholder="Pseudo" id="userId" value={data.userId} 
              type="text"
              className="block border border-grey-light w-full p-3 rounded mb-4"
            />

          <input
             onChange={(e)=>handle(e)} placeholder="Prenom" id="name" value={data.name} 
              type="text"
              className="block border border-grey-light w-full p-3 rounded mb-4"
            />

            <input
             onChange={(e)=>handle(e)} placeholder="nom" id="lastName" value={data.lastName} 
              type="text"
              className="block border border-grey-light w-full p-3 rounded mb-4"
            />

            <input
             onChange={(e)=>handle(e)} placeholder="age" id="age" value={data.age} 
              type="text"
              className="block border border-grey-light w-full p-3 rounded mb-4"
            />

            <input
             onChange={(e)=>handle(e)} placeholder="email" id="email" value={data.email} 
              type="text"
              className="block border border-grey-light w-full p-3 rounded mb-4"
            />
  
            <input
             onChange={(e)=>handle(e)} placeholder="mot de passe" id="password" value={data.password}
              type="password"
              className="block border border-grey-light w-full p-3 rounded mb-4"
            />

  
            <button
              type="submit"
              className="w-full text-center py-3 rounded bg-green focus:outline-none my-1"
            >
              submit
            </button>
  
            <div className="text-center text-sm text-grey-dark mt-4">
              By signing up, you agree to the{" "}
              <a
                className="no-underline border-b border-grey-dark text-grey-dark"
                href="#"
              >
                Terms of Service
              </a>{" "}
              and{" "}
              <a
                className="no-underline border-b border-grey-dark text-grey-dark"
                href="#"
              >
                Privacy Policy
              </a>
            </div>
          </form>
  
          <div className="text-grey-dark mt-6">
            T'as d√©ja un compte?{" "}
            <a className="no-underline border-b border-blue text-blue" href="../">
              Connectes toi
            </a>
            .
          </div>
        </div>

                      <div className="container max-w-sm mx-auto flex-1 flex flex-col items-center justify-center px-2 scrollable-container">
          <form
            className="bg-white px-6 py-8 rounded shadow-md text-black w-full"
            onSubmit={handleSubmit}
          >
            <h1 className="mb-8 text-3xl text-center">Se connecter</h1>
  
            <input
              onChange={handleEmailChange}
              placeholder="Email"
              type="text"
              className="block border border-grey-light w-full p-3 rounded mb-4"
            />
  
            <input
              onChange={handlePasswordChange}
              placeholder="Mot de passe"
              type="password"
              className="block border border-grey-light w-full p-3 rounded mb-4"
            />
  
            <button
              type="submit"
              className="w-full text-center py-3 rounded bg-green focus:outline-none my-1"
            >
              Se connecter
            </button>
  
            {error && (
              <div className="text-center text-sm text-red-500 mt-4">
                {error}
              </div>
            )}
          </form>
  
          <div className="text-grey-dark mt-6">
            Vous n'avez pas de compte?{' '}
            <a className="no-underline border-b border-blue text-blue" href="#">
              S'inscrire
            </a>
            .
          </div>
        </div>
      </div>



    );
  }


export default Inscription
