import React, {useState} from 'react'
import logo from './logo.svg';
import './index.css';
import './App.css';
import Navbar from './Navbar/Navbar';
import { Container } from '@mui/system';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

function Connexion() {
   const [email, setEmail] = useState('');
   const [password, setPassword] = useState('');
   const [error, setError] = useState('');

   function handleEmailChange(event) {
     setEmail(event.target.value);
   }

   function handlePasswordChange(event) {
     setPassword(event.target.value);
   }

   function handleSubmit(e) {
      e.preventDefault();

      // Envoi de la requête POST au serveur pour vérifier les informations d'identification
      axios
        .post("http://localhost:3000/users/login", {
          email: email,
          password: password,
        })
        .then((res) => {
          if (res.data.auth) {
            // Si les informations d'identification sont valides, redirigez l'utilisateur vers la page suivante
            history.push("/suivant");
          } else {
            // Si les informations d'identification ne sont pas valides, affichez un message d'erreur
            setError(true);
          }
        })
        .catch((err) => {
          console.log(err);
        });
    }


   return (
     <div className="bg-grey-lighter min-h-screen flex flex-col">
       <div className="container max-w-sm mx-auto flex-1 flex flex-col items-center justify-center px-2 scrollable-container">
         <form
           className="bg-white px-6 py-8 rounded shadow-md text-black w-full"
           onSubmit={handleSubmit}
         >
           <h1 className="mb-8 text-3xl text-center">Se connecter</h1>

           <input
             onChange={handleEmailChange}
             placeholder="Email"
             type="text"
             className="block border border-grey-light w-full p-3 rounded mb-4"
           />

           <input
             onChange={handlePasswordChange}
             placeholder="Mot de passe"
             type="password"
             className="block border border-grey-light w-full p-3 rounded mb-4"
           />

           <button
             type="submit"
             className="w-full text-center py-3 rounded bg-green focus:outline-none my-1"
           >
             Se connecter
           </button>

           {error && (
             <div className="text-center text-sm text-red-500 mt-4">
               {error}
             </div>
           )}
         </form>

         <div className="text-grey-dark mt-6">
           Vous n'avez pas de compte?{' '}
           <a className="no-underline border-b border-blue text-blue" href="#">
             S'inscrire
           </a>
           .
         </div>
       </div>
     </div>
   );
 }

 export default Connexion;
