import React, {useState} from 'react'
import { BrowserRouter, Route } from 'react-router-dom';
import logo from './logo.svg';
import './index.css';
import './App.css';
import Navbar from './Navbar/Navbar';
import { Container } from '@mui/system';
import axios from 'axios';
import { useHistory } from 'react-router-dom'; // import du hook useHistory pour la navigation

function Connexion() {
   const history = useHistory(); // initialisation du hook useHistory

   const url = "http://localhost:3000/users";
   const [email, setEmail] = useState('');
   const [password, setPassword] = useState('');
   const [error, setError] = useState('');

   function submit(e) {
     e.preventDefault();
     axios.get(`${url}?email=${email}&password=${password}`)
       .then(res => {
         if (res.data.length > 0) { // si l'e-mail et le mot de passe correspondent
           history.push('/page-suivante'); // redirige l'utilisateur vers la page suivante
         } else { // si les identifiants sont invalides
           setError('E-mail ou mot de passe invalide');
         }
       })
       .catch(err => console.log(err));
   }

   function handleEmailChange(e) {
     setEmail(e.target.value);
   }

   function handlePasswordChange(e) {
     setPassword(e.target.value);
   }

   return (
     <div className="bg-grey-lighter min-h-screen flex flex-col">
       <div className="container max-w-sm mx-auto flex-1 flex flex-col items-center justify-center px-2 scrollable-container">
         <form
           className="bg-white px-6 py-8 rounded shadow-md text-black w-full"
           onSubmit={(e) => submit(e)}>
           <h1 className="mb-8 text-3xl text-center">Connexion</h1>

           <input
             onChange={handleEmailChange}
             placeholder="E-mail"
             value={email}
             type="email"
             className="block border border-grey-light w-full p-3 rounded mb-4"
             required
           />

           <input
             onChange={handlePasswordChange}
             placeholder="Mot de passe"
             value={password}
             type="password"
             className="block border border-grey-light w-full p-3 rounded mb-4"
             required
           />

           {error && <p className="text-red-500">{error}</p>}

           <button
             type="submit"
             className="w-full text-center py-3 rounded bg-green focus:outline-none my-1"
           >
             Connexion
           </button>

           <div className="text-center text-sm text-grey-dark mt-4">
             <a
               className="no-underline border-b border-grey-dark text-grey-dark"
               href="#"
             >
               Mot de passe oubli√© ?
             </a>
           </div>
         </form>

         <div className="text-grey-dark mt-6">
           Pas encore de compte ?{' '}
           <a className="no-underline border-b border-blue text-blue" href="../inscription">
             Inscrivez-vous
           </a>
           .
         </div>
       </div>
     </div>
   );
 }

 export default Connexion;
