import React, {useState} from 'react'
import logo from './logo.svg';
import './index.css';
import './App.css';
import Navbar from './Navbar/Navbar';
import { Container } from '@mui/system';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

function Inscription() {
   const [data, setData] = useState({
     userId:"",
     name:"",
     lastName:"",
     age:"",
     email:"",
     password:"",
   });

   function handle(e) {
     const newdata={...data}
     newdata[e.target.id] = e.target.value
     setData(newdata)
   };

   function handleSubmit(e) {
     e.preventDefault();
     const url = "http://localhost:3000/users";
     axios.get(url)
       .then(res => {
         const users = res.data;
         const user = users.find(u => u.email === data.email && u.password === data.password);
         if (user) {
           console.log("User found:", user);
           // Rediriger l'utilisateur vers une autre page ici
         } else {
           console.log("Email or password incorrect");
         }
       })
       .catch(error => {
         console.log("An error occurred while fetching the users:", error);
       });
   }


   return (
     <div className="bg-grey-lighter min-h-screen flex flex-col">
       <div className="container max-w-sm mx-auto flex-1 flex flex-col items-center justify-center px-2 scrollable-container">
         <form
           className="bg-white px-6 py-8 rounded shadow-md text-black w-full"
           onSubmit={handleSubmit}
         >
           <h1 className="mb-8 text-3xl text-center">Se connecter</h1>

           <input
             onChange={handleEmailChange}
             placeholder="Email"
             type="text"
             className="block border border-grey-light w-full p-3 rounded mb-4"
           />

           <input
             onChange={handlePasswordChange}
             placeholder="Mot de passe"
             type="password"
             className="block border border-grey-light w-full p-3 rounded mb-4"
           />

           <button
             type="submit"
             className="w-full text-center py-3 rounded bg-green focus:outline-none my-1"
           >
             Se connecter
           </button>

           {error && (
             <div className="text-center text-sm text-red-500 mt-4">
               {error}
             </div>
           )}
         </form>

         <div className="text-grey-dark mt-6">
           Vous n'avez pas de compte?{' '}
           <a className="no-underline border-b border-blue text-blue" href="#">
             S'inscrire
           </a>
           .
         </div>
       </div>
     </div>
   );
 }

 export default Connexion;
